def sum(n1,n2):
    print(f"Sum result: {n1+2}")

def minus(n1,n2):
    print("Minus result: ",n1-n2)

def multiply(n1,n2):
    print("Multiply result: ",n1*n2)

def divide(n1,n2):
    print("Dvision result: ",n1/n2)

def round(n1):
    print(f"The rounded number is {round(n1)}")

def exp(base,exp):
    print(f"The result is {base**exp}")

